$(function(){var a="Your message has been sent.";var b="Sorry it seems that our mail server is not responding, Sorry for the inconvenience!";$("input,textarea").jqBootstrapValidation({preventSubmit:true,submitSuccess:function(c,d){d.preventDefault();var f="./includes/"+c.attr("id")+".php";var e={};c.find("input, textarea").each(function(h){var g=$(this).val();if($(this).is(":checkbox")){g=$(this).is(":checked")}else{if($(this).is(":radio")){g=$(this).val()+" = "+$(this).is(":checked")}}e[$(this).attr("id")]=g});$.ajax({url:f,type:"POST",data:e,cache:false,success:function(){c.append("<div id='form-alert'><div class='alert alert-success'><button type='button' class='close' data-dismiss='alert' aria-hidden='true'>&times;</button><strong>"+a+"</strong></div></div>")},error:function(){c.append("<div id='form-alert'><div class='alert alert-danger'><button type='button' class='close' data-dismiss='alert' aria-hidden='true'>&times;</button><strong>"+b+"</strong></div></div>")},complete:function(){c.trigger("reset")}})},filter:function(){return $(this).is(":visible")}})});